import React from 'react';
import styled from 'styled-components';
import { Accordion } from '../accordion';
import { useState } from 'react';

const Wrapper = styled.div`
  width: 690px;
  margin: 100px auto;
  font-family: MuseoSans,sans-serif;
  list-style: none;
  h3 {
    text-align: center;
    margin-bottom: 30px;
  }
`;

const AccordionWrapper = styled.div`
  div:first-child{
    border-top: none;
  }
`

const data = [
  {
    title: "Мобильный ID – что это?",
    description: [
      'Это кнопка для размещения партнером на своих сайтах и/или в приложениях, использование которой запускает функционал быстрой и безопасной авторизации (создание учетной записи) и аутентификации (подтверждение актуальности) по номеру мобильного телефона клиента.'
    ]
  }
]

const initState = new Array(9).fill(false);
export const FAQ = () => {
  const [state, setState] = useState(initState);

  const handler = (id: number) => {
    const newState = new Array(9).fill(false);
    newState[id] = !state[id];
    setState(newState)
  }

  return <Wrapper>
    <h3>Вопросы и ответы *</h3>
    <AccordionWrapper>
      {state.map((el, id) => <Accordion isOpened={state[id]} setIsOpened={handler} id={id} />)}
    </AccordionWrapper>
  </Wrapper>
}
