import React from "react";
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 250px;
  height: 501px;
  position: absolute;
  box-shadow: inherit;
  right: 80px;
  top: -105px;
  background-image: url("https://mobilestream.ru//landing/images/mobileid/iphone-transparent.png");
  background-size: contain;
  div {
    overflow: hidden;
    display: flex;
    height: 382px;
    width: 218px;
    top: 62px;
    left: 16px;
    position: relative;
  }
`

const Controllers = styled.div`
  display: flex;
  justify-content: space-between;
  position: absolute;
  width: 86px;

`

const Controller = styled.div`
  width: 10px;
  height: 10px;
  border-radius: 5px;
  background-color: #fff;
  cursor: pointer;
  transition: background-color .3s
`

export const Slider: React.FC<ISlider> = props => (
    <Wrapper>
        <div>
            {props.imgArray.map(img => <img src={img} alt="slide" />)}
        </div>
        <Controllers>
            {props.imgArray.map(img => <Controller key={img}/>)}
        </Controllers>
    </Wrapper>
);

interface ISlider {
    imgArray: string[];
}
