import React, {useState} from "react";
import styled from 'styled-components';

const Wrapper = styled.div<ISliderWrapper>`
  width: ${props => props.width || '250px'};
  height: ${props => props.height || '501px'};
  position: ${props => props.position || 'absolute'};
  right: ${props => props.right || '80px'};
  top: ${props => props.top || '-105px'};
  background-image: ${props => props.removeBackground ? 'none' : 'url("https://mobilestream.ru//landing/images/mobileid/iphone-transparent.png")'};
  background-size: contain;
  box-shadow: ${props => props.boxShadow || 'inherit'};
  .wrapper {
    overflow: hidden;
    display: flex;
    height: ${props => props.imgHeight || '382px'};
    width: ${props => props.imgWidth || '218px'};
    top: 62px;
    left: 16px;
    position: ${props => props.imgPosition || 'absolute'};
  }
  img {
    opacity: 0;
    position: absolute;
  }
  .active {
    opacity: 1;
    transition-duration: .5s;
    transition-property: opacity;
    transition-timing-function: ease-in-out;
  }
`

const Controllers = styled.div`
  display: flex;
  justify-content: space-between;
  position: absolute;
  width: 86px;
  bottom: -40px;
  margin-left: 50%;
  transform: translateX(-50%);
  .active {
    background-color: #c000d9;
  }
`

const Controller = styled.div<IController>`
  width: 10px;
  height: 10px;
  border-radius: 5px;
  background-color: ${props => props.controllerColor || '#fff'};
  cursor: pointer;
  transition: background-color .3s;
`

export const Slider: React.FC<ISlider> = ({controllerColor,  imgArray, ...props}) => {
    const [selectedImg, setSelectedImg] = useState(0);
    const handleControllerClick = (id: number) => {
        setSelectedImg(id)
    }
    return(
        <Wrapper {...props}>
            <div className="wrapper">
                {imgArray.map((img, id) =>
                        <img src={img}
                             alt="slide"
                             key={img}
                             className={id === selectedImg ? 'active' : ''}
                        />
                    )}
            </div>
            <Controllers>
                {imgArray.map((img, id) =>
                        <Controller
                            controllerColor={controllerColor}
                            key={img}
                            className={id === selectedImg ? 'active' : ''}
                            onClick={() => handleControllerClick(id)}
                        />
                    )}
            </Controllers>
        </Wrapper>
    )
};

interface ISlider extends IController, ISliderWrapper {
    imgArray: string[];
}

interface IController {
    controllerColor?: string;
}

interface ISliderWrapper {
    width?: string;
    height?: string;
    position?: string;
    removeBackground?: boolean;
    right?: string;
    top?: string;
    imgWidth?: string;
    imgHeight?: string;
    imgPosition?: string;
    boxShadow?: string;
}
