import React from 'react'
import styled from 'styled-components';

const Title = styled.div`
  display: flex;
  position: relative;
  padding: 30px 30px 30px 31px;
  align-items: center;
  :before {
    position: absolute;
    top: 0;
    right: 0;
    width: 10px;
    height: 100%;
    content: "";
    transition: all .4s ease;
    background-image: url('/static/img/accordion/down.svg');
    background-repeat: no-repeat;
    background-position: 50%;
    background-size: 10px;
  }

`

const Wrapper = styled.div`
  position: relative;
  cursor: pointer;  
  border-top: 1px solid #f2f2f2;
  padding: 0 0 25px 31px;
  //list-style: none;
  span {
    font-family: MuseoSansMedium,sans-serif;
    font-size: 18px;
    font-weight: 700;
  }
`;

const Icon = styled.img.attrs({src: '/static/img/accordion/accordion-icon.svg'})`
  margin-right: 20px;
`
const Description = styled.ul`
  display: block;
  padding: 0 0 25px 31px;
  list-style: none;
  //margin-bottom: 20px;
  font-size: 18px;
  font-weight: 300;
  line-height: 1.56;
  color: #5a6d73;
`

export const Accordion = () => {
  return <Wrapper>
    <Title><Icon/> <span>Мобильный ID – что это?</span></Title>
    <Description>
      <li>
        Это кнопка для размещения партнером на своих сайтах и/или в приложениях, использование которой запускает функционал
        быстрой и безопасной авторизации (создание учетной записи) и аутентификации (подтверждение актуальности) по номеру мобильного телефона клиента.
      </li>
    </Description>
  </Wrapper>
}
