import React, {useState} from "react";
import styled from 'styled-components';

const Wrapper = styled.div<ISliderWrapper>`
  width: 250px;
  height: 501px;
  position: absolute;
  box-shadow: inherit;
  right: 80px;
  top: -105px;
  background-image: url("https://mobilestream.ru//landing/images/mobileid/iphone-transparent.png");
  background-size: contain;
  .wrapper {
    overflow: hidden;
    display: flex;
    height: 382px;
    width: 218px;
    top: 62px;
    left: 16px;
    position: absolute;
  }
  img {
    opacity: 0;
    position: absolute;
  }
  .active {
    opacity: 1;
    transition-duration: .5s;
    transition-property: opacity;
    transition-timing-function: ease-in-out;
  }
`

const Controllers = styled.div`
  display: flex;
  justify-content: space-between;
  position: absolute;
  width: 86px;
  bottom: -40px;
  margin-left: 50%;
  transform: translateX(-50%);
  .active {
    background-color: #c000d9;
  }
`

const Controller = styled.div<IController>`
  width: 10px;
  height: 10px;
  border-radius: 5px;
  background-color: ${props => props.controllerColor || '#fff'};
  cursor: pointer;
  transition: background-color .3s;
`

export const Slider: React.FC<ISlider> = ({controllerColor,  imgArray, ...props}) => {
    const [selectedImg, setSelectedImg] = useState(0);
    const handleControllerClick = (id: number) => {
        setSelectedImg(id)
    }
    return(
        <Wrapper>
            <div className="wrapper">
                {imgArray.map((img, id) =>
                        <img src={img}
                             alt="slide"
                             key={img}
                             className={id === selectedImg ? 'active' : ''}
                        />
                    )}
            </div>
            <Controllers>
                {imgArray.map((img, id) =>
                        <Controller
                            controllerColor={controllerColor}
                            key={img}
                            className={id === selectedImg ? 'active' : ''}
                            onClick={() => handleControllerClick(id)}
                        />
                    )}
            </Controllers>
        </Wrapper>
    )
};

interface ISlider extends IController, ISliderWrapper {
    imgArray: string[];
}

interface IController {
    controllerColor?: string;
}

interface ISliderWrapper {
    width?: string;
    height?: string;
}
