import React from "react";
import styled from 'styled-components';
import {Slider} from "../slider";

const Wrapper = styled.div<IWrapper>`
  margin: 200px auto;
  background-color:${props => props.backgroundColor};
  position: relative;
  display: flex;
  width: 968px;
  height: auto;
  font-family: MuseoSansCyrl,sans-serif;
  line-height: 1.56;
  z-index: 0;
  color: #fff;
  ul {
    padding: 0;
    line-height:28.08px;
    li {
        line-height: 28px;
        list-style: none;
        padding: 5px 0 5px 24px;
        font-size: 18px;
        position: relative;
    }
    li::before {
        position: absolute;
        left: 0;
        width: 8px;
        height: 8px;
        margin-top: -4px;
        content: "";
        border-radius: 100%;
        background-color: #fff;
        top: 50%;
    }
  }
  ol {
    display: flex;
    flex-direction: column;
    font-size: 18px;
    padding-left: 24px;
    margin-top: 25px;
    margin-bottom: 35px;
    font-weight: 300;
    justify-content: space-evenly;
    list-style: none;
    counter-reset: numeric-counter;

    li {
       counter-increment: numeric-counter;
       position: relative;
       list-style-type: none;
       display: list-item;
      }
    li:before {
       content: counter(numeric-counter) ".";
       font-weight: 700;
       position: absolute;
       left: -24px;
    }
  }
`
const Info = styled.div`    
    margin-top: 58px;
    margin-bottom: 58px;
    margin-left: 99px;
    width: 444px;
    @media (max-width: 1279px) {
        width: auto;
        margin: 490px 58px 58px;
    }
`

const Title = styled.div`
  font-size: 26px;
  font-weight: 600;
`
const SubTitle = styled.div`
    font-size: 23px;
    font-weight: 600;
`

export const QuickAuthInfo: React.FC<ISliderWithDescription> = props => {
    return <Wrapper {...props}>
        <Info>
            <Title>Быстрая авторизация</Title>
            При использовании мобильного интернета, клиенту даже не придется вводить номер телефона. Оператор определит его автоматически, вход осуществляется при выборе клиентом кнопки «Мобильный ID» и подтверждением входа в Push-сообщении нажатием кнопки «ОК»
            <SubTitle>Возможности</SubTitle>
            <ul>
                <li>Легкий вход и регистрация на сайтах по номеру телефона</li>
                <li>Проверка номера вашего клиента на актуальность</li>
                <li>Подтверждение действия, операций, заказа, платежной транзакции через клик или ввод ПИН-кода в телефоне</li>
            </ul>
        </Info>
        <Slider imgArray={[
            '/static/img/slider-2/2.1.png',
            '/static/img/slider-2/2.2.png',
            '/static/img/slider-2/2.3.png',
            '/static/img/slider-2/2.4.png',
            '/static/img/slider-2/2.5.png'
        ]}/>
    </Wrapper>
}

interface ISliderWithDescription {
    backgroundColor: string;
}

interface IWrapper {
    backgroundColor: string;
}
