import React from 'react'
import styled from 'styled-components';

const Wrapper = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  padding: 30px 30px 30px 31px;
  cursor: pointer;  
  border-top: 1px solid #f2f2f2;
  span {
    font-family: MuseoSansMedium,sans-serif;
    font-size: 18px;
    font-weight: 700;
  }
  :before {
    position: absolute;
    top: 0;
    right: 0;
    width: 10px;
    height: 100%;
    content: "";
    transition: all .4s ease;
    background-image: url('/static/img/accordion/down.svg');
    background-repeat: no-repeat;
    background-position: 50%;
    background-size: 10px;
  }
`;

const Icon = styled.img.attrs({src: '/static/img/accordion/accordion-icon.svg'})`
  margin-right: 20px;
`
const Description = styled.div`
  display: block;
  padding: 0 0 25px 31px;
  list-style: none;
  color: #2e3c40;
`

export const Accordion = () => {
  return <Wrapper>
    <Icon/> <span>Мобильный ID – что это?</span>
    <Description></Description>
  </Wrapper>
}
