import React from "react";
import styled from 'styled-components';

const Wrapper = styled.div<IWrapper>`
  background-color:${props => props.backgroundColor};
  position: relative;
  display: flex;
  width: 968px;
  height: auto;
  margin: auto;
  font-family: MuseoSansCyrl,sans-serif;
  line-height: 1.56;
  z-index: 0;
  color: #fff;
  //li {
  //  counter-increment: numeric-counter;
  //  position: relative;
  //}
  li:before {
   content: counter(numeric-counter) ".";
   font-weight: 700;
   position: absolute;
   left: -24px;
  }
`
const Info = styled.div`    
    margin-top: 58px;
    margin-bottom: 58px;
    margin-left: 99px;
    width: 444px;
    border:1px solid red;
    @media (max-width: 1279px) {
        width: auto;
        margin: 490px 58px 58px;
    }
`

const Title = styled.div`
  font-size: 26px;
  font-weight: 600;
`

export const SliderWithDescription: React.FC<ISliderWithDescription> = props => {
    return <Wrapper {...props}>
        <Info>
            <Title>Авторизация с вводом номера</Title>
            <ol>
                <li>Клиент на сайте выбирает как способ входа или регистрации «Мобильный ID»</li>
                <li>Вводит свой номер и получает на телефон Push - сообщение</li>
            </ol>
        </Info>
    </Wrapper>
}

interface ISliderWithDescription {
    backgroundColor: string;
}

interface IWrapper {
    backgroundColor: string;
}
