import React,  {useState, useRef} from 'react'
import styled, {keyframes} from 'styled-components';
const openAnimation = keyframes`
  from {
    display: block;
    max-height: 1000px;
  }
  99% {
    display: block;
    height: 0;
  }
  to {
    display: none;
    height: 0;
  }
`
const closeAnimation = keyframes`
  from {
    display: none;
    height: 0;
  }
  1% {
    display: block;
    height: 0;
  }
  to {
    display: block;
    max-height: 1000px;
  }
`
const Title = styled.div`
  cursor: pointer;  
  display: flex;
  position: relative;
  padding: 30px 30px 30px 31px;
  align-items: center;
  :before {
    position: absolute;
    top: 0;
    right: 0;
    width: 10px;
    height: 100%;
    content: "";
    transition: all .4s ease;
    background-image: url('/static/img/accordion/down.svg');
    background-repeat: no-repeat;
    background-position: 50%;
    background-size: 10px;
  }

`

const Wrapper = styled.div`
  position: relative;
  border-top: 1px solid #f2f2f2;
  padding: 0 0 25px 31px;
  span {
    font-family: MuseoSansMedium,sans-serif;
    font-size: 18px;
    font-weight: 700;
  }
`;

const Icon = styled.i`
  margin-right: 20px;
  background-image: url('/static/img/accordion/accordion-icon.svg');
  position: absolute;
  top: 50%;
  left: 0;
  width: 15px;
  height: 20px;
  transform: translateY(-50%);
  background-repeat: no-repeat;
  background-position: 50%;
  background-size: contain;
`
const Description = styled.ul`
  display: block;
  list-style: none;
  padding: 0 0 25px 31px;
  font-size: 18px;
  font-weight: 300;
  line-height: 1.56;
  color: #5a6d73;
  margin: 0;
  transition-duration: 1s;
  transition-timing-function: ease-in-out;
  transition-property: max-height;
  overflow: hidden;
  max-height: 1000px;
  &.closed {
    max-height: 0; 
  }
  li {
    display: block;
    padding: 16px 0;
  }
  div {
    margin-bottom: 20px;
  }
`

export const Accordion = () => {
  const [state, setState] = useState(false)
  const descRef = useRef<HTMLUListElement>(null);
  const handleClick = () => {
    setState(prevState => {
      if(!prevState) {
        descRef.current.style.display = 'block'
      }
      return !prevState;
    });
  }
  const transitionEndHandler = () => {
    console.log(state)
    descRef.current.style.display = state ? 'block' : 'none';
  }

  return <Wrapper>
    <Title onClick={handleClick}><Icon/> <span>Мобильный ID – что это?</span></Title>
    <Description ref={descRef} className={state ? '' : 'closed'} onTransitionEnd={transitionEndHandler}>
      <li>
        <div>
          Это кнопка для размещения партнером на своих сайтах и/или в приложениях, использование которой запускает функционал
          быстрой и безопасной авторизации (создание учетной записи) и аутентификации (подтверждение актуальности) по номеру мобильного телефона клиента.
        </div>
        <div>
          В случаях, когда предъявляются повышенные требования к безопасности для подтверждения действия запрашивается PIN-код.
        </div>
      </li>
    </Description>
  </Wrapper>
}
