import React, {useState} from "react";
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 250px;
  height: 501px;
  position: absolute;
  box-shadow: inherit;
  right: 80px;
  top: -105px;
  background-image: url("https://mobilestream.ru//landing/images/mobileid/iphone-transparent.png");
  background-size: contain;
  .wrapper {
    overflow: hidden;
    display: flex;
    height: 382px;
    //width: 218px;
    width: 211px;
    top: 62px;
    left: 16px;
    position: relative;
  }
`

const Controllers = styled.div`
  display: flex;
  justify-content: space-between;
  position: absolute;
  width: 86px;
  bottom: -40px;
  margin-left: 50%;
  transform: translateX(-50%);

`

const Controller = styled.div`
  width: 10px;
  height: 10px;
  border-radius: 5px;
  background-color: #fff;
  cursor: pointer;
  transition: background-color .3s
`

const ImgWrapper = styled.div<{selectedImg?: number}>`
  display:flex;
  transition-duration: .5s;
  transition-timing-function: ease-in-out;
`

export const Slider: React.FC<ISlider> = props => {
    const [selectedImg, setSelectedImg] = useState(0);
    const handleControllerClick = (id: number) => {
        setSelectedImg(id)
    }
    return(
        <Wrapper>
            <div className="wrapper">
                {props.imgArray.map((img, id) => <img src={img} alt="slide" key={img} className={id === selectedImg ? 'active' : ''}/>)}
            </div>
            <Controllers>
                {props.imgArray.map((img, id) => <Controller key={img} onClick={() => handleControllerClick(id)}/>)}
            </Controllers>
        </Wrapper>
    )
};

interface ISlider {
    imgArray: string[];
}
