import styled from 'styled-components'
import React from "react";

const ButtonConsultWrapper = styled.div<IButton>`
    font-size: 18px;
    line-height: 60px;
    display: inline-block;
    width: 211px;
    // min-width: 220px;
    height: 60px;
    padding: 0 38px;
    cursor: pointer;
    -webkit-transition: background-color .3s ease;
    transition: background-color .3s ease;
    text-align: center;
    text-decoration: none;
    color: #fff;
    border: none;
    border-radius: 5px;
    background-color: #c000d9;
    box-shadow: none;
    margin: ${props => props.margin}
    :hover {
      background-color: #de04fa;
    }
`
export const ButtonConsult: React.FC<IButton> = props => {
    return <ButtonConsultWrapper {...props}>
        Получить консультацию
    </ButtonConsultWrapper>
};

export const ButtonDemo = () => {
    return <ButtonConsultWrapper>
        Демо доступ
    </ButtonConsultWrapper>
}

interface IButton {
    margin?: string;
}
