import React from "react";
import styled from 'styled-components';
import {Slider} from "../slider";
import {Info, ISliderWithDescription, SliderWrapper, SubTitle, Title} from "../auth-info";

const Wrapper = styled(SliderWrapper)`
  margin: 200px auto;
  color: #001424;
`

const Description = styled.div`
    width: 468px;
    padding: 0;
    display: flex;
    flex-direction: column;
    font-size: 18px;
    margin-top: 25px;
    margin-bottom: 35px;
    font-weight: 300;
`

const AuthPinTitle = styled(Title)`
    font-size: 26px;
    font-weight: 600;
    font-family: MuseoSans,sans-serif;
`

export const AuthPin: React.FC<ISliderWithDescription> = props => {
    return <Wrapper {...props}>
        <Info>
            <AuthPinTitle>Авторизация с вводом PIN-кода *</AuthPinTitle>
            <Description>На сайтах с повышенными требованиями к безопасности, а также где в онлайн требуется подтверждение значимых операций, заказа, платежной транзакции, реализовано функционал подтверждения с помощью ввода ПИН-кода, который создается клиентом и храниться на SIM-карте телефона, обеспечивая высокую надежность и защищенность данных</Description>
            <SubTitle>Возможности</SubTitle>
            <ul>
                <li>Единый PIN-код для всех операций</li>
                <li>Передача персонализированных данных с согласия клиента только после получения явного подтверждения</li>
                <li>Проверка персонализированных данных с согласия клиента в онлайн сервисе</li>
            </ul>
        </Info>
        <Slider imgArray={[
            '/static/img/slider-2/2.1.png',
            '/static/img/slider-2/2.2.png',
            '/static/img/slider-2/2.3.png',
            '/static/img/slider-2/2.4.png'
        ]}/>
    </Wrapper>
}
