import React from "react";
import styled from 'styled-components';

const Wrapper = styled.div<IWrapper>`
  background-color:${props => props.backgroundColor};
  position: relative;
  display: flex;
  width: 968px;
  height: auto;
  margin: auto;
  font-family: MuseoSansCyrl,sans-serif;
  line-height: 1.56;
  z-index: 0;
  color: #fff;
  .info ol {
    padding: 0;
    li {
        list-style: disc;
        line-height: inherit;
        padding-bottom: 5px;
        padding-top: 5px;
        font-size: 18px
    }
    li::marker {
        background-color: #fff;
        top: 18.04px;
        position: absolute;
        left: 0;
        width: 8px;
        height: 8px;
        margin-top: -4px;
        content: "";
        border-radius: 100%
    }
  }
  ol {
    display: flex;
    flex-direction: column;
    font-size: 18px;
    padding-left: 24px;
    margin-top: 25px;
    margin-bottom: 35px;
    font-weight: 300;
    justify-content: space-evenly;
    list-style: none;
    counter-reset: numeric-counter;

    li {
       counter-increment: numeric-counter;
       position: relative;
       list-style-type: none;
       display: list-item;
      }
    li:before {
       content: counter(numeric-counter) ".";
       font-weight: 700;
       position: absolute;
       left: -24px;
    }
  }
`
const Info = styled.div`    
    margin-top: 58px;
    margin-bottom: 58px;
    margin-left: 99px;
    width: 444px;
    @media (max-width: 1279px) {
        width: auto;
        margin: 490px 58px 58px;
    }
`

const Title = styled.div`
  font-size: 26px;
  font-weight: 600;
`
const SubTitle = styled.div`
    font-size: 23px;
    font-weight: 600;
`

export const SliderWithDescription: React.FC<ISliderWithDescription> = props => {
    return <Wrapper {...props}>
        <Info>
            <Title>Авторизация с вводом номера</Title>
            <ol>
                <li>Клиент на сайте выбирает как способ входа или регистрации «Мобильный ID»</li>
                <li>Вводит свой номер и получает на телефон Push - сообщение</li>
                <li>На указанный номер придет Push-сообщение с предложением подтвердить вход и передачу данных, нажатием кнопки «Ок»</li>
            </ol>
            <SubTitle>Возможности</SubTitle>
            <ol className="info">
                <li>Минимальное количество шагов</li>
                <li>Безопасность передачи данных</li>
                <li>Работает на любых устройствах: компьютер, планшет, смартфон, кнопочный телефон</li>
            </ol>
        </Info>
    </Wrapper>
}

interface ISliderWithDescription {
    backgroundColor: string;
}

interface IWrapper {
    backgroundColor: string;
}
